import { useCallback, useEffect } from 'react'
import { ProgressBar } from '../modalStatusConversion/progressbar'
import {
  type FFmpegStoreType,
  useFFmpegStore,
  type IStatusDownload,
} from '@/store/ffmpegStore'

export default function StatusDownload() {
  const { statusDownload, setStatusDownload } = useFFmpegStore<FFmpegStoreType>(
    (state) => state,
  )

  const statusProgress = useCallback((_event: any, action: IStatusDownload) => {
    setStatusDownload(action)
    if (
      action.completed &&
      action.message === 'FFmpeg downloaded and extracted successfully'
    ) {
      setTimeout(() => {
        setStatusDownload(null)
      }, 2000)
    }
  }, [])

  useEffect(() => {
    window.electron.receive('download-ffmpeg-status', statusProgress)
    return () => {
      window.electron.removeListener('download-ffmpeg-status', statusProgress)
    }
  }, [])

  // Event key escape to close the modal
  useEffect(() => {
    const handleKeyPress = (event: any) => {
      if (event.key === 'Escape') {
        setStatusDownload(null)
      }
    }
    window.addEventListener('keydown', handleKeyPress)
    return () => {
      window.removeEventListener('keydown', handleKeyPress)
    }
  }, [])

  return (
    <div>
      {statusDownload !== null && (
        <div
          className={
            'fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-30'
          }
        >
          <section
            onClick={(event) => {
              event.stopPropagation()
            }}
            className="relative flex flex-col bg-zinc-900 rounded-md p-6 gap-2 w-full max-w-xl"
          >
            <p className="text-xl">
              {statusDownload.completed
                ? statusDownload.message
                : statusDownload.error !== ''
                  ? statusDownload.error
                  : statusDownload.message}
            </p>
            <p className="text-sm">
              {statusDownload.completed
                ? statusDownload.totalLength
                : statusDownload.elapsedLength}
              {statusDownload.error !== '' ? '' : ' / '}
              {statusDownload.totalLength}
            </p>
            <ProgressBar
              value={
                statusDownload.completed
                  ? '100'
                  : statusDownload.error !== ''
                    ? 'error'
                    : statusDownload.percentage.toString()
              }
            />
          </section>
        </div>
      )}
    </div>
  )
}

import { useEffect } from 'react'
import ButtonComponent from '../configurationPanel/button'
import { type FFmpegStoreType, useFFmpegStore } from '@/store/ffmpegStore'
import { IconCheck } from './IconCheck'
import { IconWarning } from './IconWarning'
enum ffmpegMessages {
  FOUND = 'FFmpeg correctly installed',
}
export default function StatusFFmpeg() {
  const {
    isFFmpegInstalled,
    showModalStatus,
    setShowModalStatus,
    setIsFFmpegInstalled,
    setMessageFFmpegError,
    messageFFmpegError,
    setStatusDownload,
  } = useFFmpegStore<FFmpegStoreType>((state) => state)

  // Listen to the ffmpeg-status event
  useEffect(() => {
    const debugParams = async (_event: any, action: string) => {
      if (action === ffmpegMessages.FOUND) {
        setIsFFmpegInstalled(true)
      }
      if (action !== ffmpegMessages.FOUND) {
        setMessageFFmpegError(action)
        setIsFFmpegInstalled(false)
      }
      setShowModalStatus(true)
    }

    window.electron.receive('ffmpeg-status', debugParams)

    return () => {
      window.electron.removeListener('ffmpeg-status', debugParams)
    }
  }, [])

  // Event key escape to close the modal
  useEffect(() => {
    const handleKeyPress = (event: any) => {
      if (event.key === 'Escape') {
        setShowModalStatus(false)
      }
    }
    window.addEventListener('keydown', handleKeyPress)
    return () => {
      window.removeEventListener('keydown', handleKeyPress)
    }
  }, [])

  return (
    <div>
      {showModalStatus && (
        <div
          className={
            'fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-30'
          }
        >
          <section
            onClick={(event) => {
              event.stopPropagation()
            }}
            className="relative flex flex-col bg-zinc-900 rounded-md p-6 gap-2 w-full max-w-xl"
          >
            {isFFmpegInstalled ? (
              <div className="flex justify-between">
                <span className="flex items-center">
                  <p className="text-xl">FFmpeg is correctly installed</p>
                  <IconCheck />
                </span>
                <ButtonComponent
                  label="Close"
                  onClick={() => {
                    setShowModalStatus(false)
                  }}
                  style="py-1 w-16 self-end mr-2 mt-2"
                />
              </div>
            ) : (
              <div className="flex flex-col">
                <span className="flex items-center">
                  <p className="text-xl">{messageFFmpegError}</p>
                  <IconWarning />
                </span>
                <div className="flex justify-around my-6">
                  <ButtonComponent
                    label="Find ffmpeg in system"
                    onClick={() => {
                      window.electron.sendEvent('find-ffmpeg')
                    }}
                    style="py-1 w-16 text-xs"
                  />
                  <ButtonComponent
                    label="Download FFmpeg"
                    onClick={() => {
                      setStatusDownload({
                        error: '',
                        message: 'Downloading FFmpeg...',
                        percentage: 0,
                        totalLength: '0',
                        elapsedLength: '0',
                        completed: false,
                      })
                      window.electron.sendEvent('download-ffmpeg')
                    }}
                    style="py-1 w-16 text-xs"
                  />
                </div>
                <ButtonComponent
                  label="Close"
                  onClick={() => {
                    setShowModalStatus(false)
                  }}
                  style="py-1 w-16 self-end mr-2 mt-2 bg-yellow-400 hover:bg-yellow-700"
                />
              </div>
            )}
          </section>
        </div>
      )}
    </div>
  )
}

import { useCallback, useEffect, useState } from 'react'
import { ProgressBar } from '../modalStatusConversion/progressbar'

interface IDowloadProgress {
  bytesPerSecond: number
  percent: number
  transferred: number
  total: number
}

export default function ModalUpdateStatus() {
  const [updateDownload, setUpdateDownload] = useState<IDowloadProgress | null>(
    null,
  )

  // const debug = useCallback((_event: any, action: IDowloadProgress) => {
  //   console.log('debug: ', action)
  // }, [])

  // useEffect(() => {
  //   window.electron.receive('debug', debug)
  //   return () => {
  //     window.electron.removeListener('debug', updateDownloadProgress)
  //   }
  // }, [])

  const updateDownloadProgress = useCallback(
    (_event: any, action: IDowloadProgress) => {
      console.log(action)
      setUpdateDownload(action)
    },
    [],
  )

  useEffect(() => {
    window.electron.receive('update-download-progress', updateDownloadProgress)
    return () => {
      window.electron.removeListener(
        'update-download-progress',
        updateDownloadProgress,
      )
    }
  }, [])

  const formatBytes = (bytes: number, decimals = 2) => {
    if (bytes === 0) return '0 Bytes'

    const k = 1024
    const dm = decimals < 0 ? 0 : decimals
    const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB']

    const i = Math.floor(Math.log(bytes) / Math.log(k))

    return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i]
  }

  return (
    <div>
      {updateDownload !== null && (
        <div
          className={
            'fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-30'
          }
        >
          <section
            onClick={(event) => {
              event.stopPropagation()
            }}
            className="relative flex flex-col bg-zinc-900 rounded-md p-6 gap-2 w-full max-w-md"
          >
            <p className="text-xl">{'Downloading update...'}</p>
            <p className="text-xs">
              {'Speed: '} {formatBytes(updateDownload.bytesPerSecond)}/s
            </p>
            <p className="text-xs">
              {'Transferred: '}
              {formatBytes(updateDownload.transferred)} /{' '}
              {formatBytes(updateDownload.total)}
            </p>
            <ProgressBar value={updateDownload.percent.toFixed(0) + ''} />
          </section>
        </div>
      )}
    </div>
  )
}

